            checkIfUserExists(emailCorrect, act, view)

        }
        return view

    }

    private fun checkIfUserExists(emailCorrect: Boolean, act: MainActivity, view: View) {
        if (emailCorrect && !act.userArrayList.any()) {
            addUser(act)
            Navigation.findNavController(view).popBackStack()
            Toast.makeText(this.context, "User added", Toast.LENGTH_SHORT).show()
        } else if (emailCorrect && act.userArrayList.any()) {
            act.userArrayList.forEach {
                if (it.login == loginEditText.text.toString()) {
                    Toast.makeText(this.context, "Login name already taken", Toast.LENGTH_SHORT).show()
                } else if (it.email == emailEditText.text.toString()) {
                    Toast.makeText(this.context, "E-mail already used", Toast.LENGTH_SHORT).show()
                } else {
                    addUser(act)
                    Toast.makeText(this.context, "User added", Toast.LENGTH_SHORT).show()
                    Navigation.findNavController(view).popBackStack()
                }
            }
        } else {
            Toast.makeText(this.context, "Wrong e-mail", Toast.LENGTH_SHORT).show()
        }
    }

    private fun addUser(act: MainActivity) {
        act.userArrayList.add(
            User(
                login = loginEditText.text.toString(),
                password = passwordEditText.text.toString(),
                email = emailEditText.text.toString()
            )
        )
    }
